let dataFromESP = msg.payload;

// เตรียมข้อมูลแต่ละส่วนเพื่อสร้างแถวใหม่
let timestamp = new Date().toLocaleString("th-TH", { timeZone: "Asia/Bangkok" });

// ดึงข้อมูลจาก object ที่ได้รับมา
let triggerReason = dataFromESP.trigger;
