let pump2_status = dataFromESP.pumps.p2;
let pump3_status = dataFromESP.pumps.p3;
let soil_temp = dataFromESP.sensors.soil_temp;
let soil_moisture = dataFromESP.sensors.soil_moisture;
let soil_ph = dataFromESP.sensors.soil_ph;
let air_temp = dataFromESP.sensors.air_temp;
let air_moisture = dataFromESP.sensors.air_moisture;
