// รับข้อมูลทั้งหมดจาก Sheet เข้ามา
var data = msg.payload;
var total_temp = 0;

// คำนวณหาค่าอุณหภูมิเฉลี่ยจากข้อมูลทั้งหมด
for (var i = 0; i < data.length; i++) {
    total_temp += Number(data[i].temperature);
}
var avg_temp = total_temp / data.length;

// "คิด" หาระยะเวลารดน้ำที่เหมาะสม
// สูตรง่ายๆ: ถ้ายิ่งร้อน ยิ่งรดนาน
var watering_duration = 15; // ค่าพื้นฐาน 15 นาที
if (avg_temp > 30) {
    watering_duration = 20;
}

// ส่ง "คำตัดสินใจ" (ระยะเวลา) ออกไป
msg.payload = watering_duration;
return msg;

// รับ "ข้อมูลพยากรณ์อากาศ" ที่ส่งมาจากโหนด openweathermap
const weatherData = msg.payload;

// ตั้งค่าการตัดสินใจเริ่มต้น
let decision = "PROCEED"; // ปกติแล้วจะให้ทำงาน (เดินหน้าต่อ)
let reason = "Weather is clear."; // เหตุผลเริ่มต้น

// --- เริ่มเงื่อนไขการตัดสินใจของ AI ---
// เช็คจากสภาพอากาศโดยรวม (property ชื่อ "weather")
// ถ้าในข้อความมีคำว่า "Rain", "Thunderstorm", "Drizzle" (ฝนตก, พายุ, ฝนปรอยๆ)
// ให้เปลี่ยนการตัดสินใจ
if (weatherData.weather.includes("Rain") ||
    weatherData.weather.includes("Thunderstorm") ||
    weatherData.weather.includes("Drizzle")) {

    decision = "SKIP"; // ตัดสินใจว่า "ข้าม" การทำงานนี้ไป
    reason = "High chance of rain detected.";
}

// เราจะเปลี่ยน msg.payload ให้เป็น "คำตัดสินใจ" ของ AI แทน
msg.payload = {
    decision: decision,
    reason: reason,
    weather_detail: weatherData.detail // ส่งรายละเอียดสภาพอากาศจริงไปด้วย
};

return msg;

// ดึงข้อมูลการตัดสินใจจาก AI
